package com.syntax.class02;

public class ArithmeticOperation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int number1=10;
	int number2=20;
    System.out.println(number1+number2);
		    System.out.println(number1-number2);
		    System.out.println(number1*number2);
		    System.out.println(number1/number2);
		    System.out.println("****************");
		    double number3=10;
		    double number4=20;
		    System.out.println(number3+number4);
		    System.out.println(number3-number4);
		    System.out.println(number3*number4);
		    System.out.println(number3/number4);
		    System.out.println("****************");
		    System.out.println(10/20.5);
		    System.out.println("********************");
		    // int number5=10/20.5; not allowed
		    //System.out.println(number5);
		    boolean var1=true;
		    boolean var2=false;
		    //System.out,println(var/var2); not allowedon booleans
		    System.out.println(var1);
		    System.out.println(var2);
		    System.out.println("*****************");
    char char1='A';
    char char2='B';
    // SCII table form have to check
    System.out.println(char1+char2);
    System.out.println(char1-char2);
    System.out.println(char1*char2);
    System.out.println(char1/char2);
    System.out.println("*****************");
    String str1="hi";
    String str2="bye";
    System.out.println(str1+str2);
    /*
     * System.out.println(str1-str2); not allowed
     * System.out.println(str1*str2); not allowed
     * System.out.println(str1/str2); not allowed
     */
    		
	}

}
;