package com.syntax.class03;

public class EvenOrOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    double temp=12;
    if (temp%3==1) {
    	System.out.println("the number "+ temp+" is Even");
    } else {
    	System.out.println("the number "+temp +" is Odd");
    }
	}

}
